```java
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.URL;

public class BatchDeleteContacts {
    public static void main(String[] args) throws Exception {
        String url = "https://people.googleapis.com/v1/people:batchDeleteContacts";
        URL obj = new URL(url);
        HttpURLConnection con = (HttpURLConnection) obj.openConnection();
        con.setRequestMethod("POST");
        con.setRequestProperty("Authorization", "Bearer YOUR_ACCESS_TOKEN");
        con.setDoOutput(true);

        String jsonInputString = "{\"resourceNames\":[\"people/12345\", \"people/67890\"]}";
        try (OutputStream os = con.getOutputStream()) {
            byte[] input = jsonInputString.getBytes("utf-8");
            os.write(input, 0, input.length);
        }

        int responseCode = con.getResponseCode();
        System.out.println("Response Code : " + responseCode);
    }
}
```

